import React, { useState } from "react";
import './ExpenseForm.css';

const ExpenseForm = (props) => {

    // Using one state
    // const [userInput, setUserInput] = useState({
    //     enderedTitle: '',
    //     enderedAmount: '',
    //     enderedDate: ''
    // });
    // const titleChangeHandler = (event) => {
    //     setUserInput({
    //         ...userInput,
    //         enderedTitle: event.target.value,
    //     });
    //     setUserInput((prevState) => {
    //         return {...prevState, enderedTitle: event.target.value };
    //     });
    // };
    // const amountChangeHandler = (event) => {
    //     setUserInput({
    //         ...userInput,
    //         enderedAmount: event.target.value,
    //     });
    // };
    // const dateChangeHandler = (event) => {
    //     setUserInput({
    //         ...userInput,
    //         enderedDate: event.target.value,
    //     });
    // };


    const [enderedTitle, setEnteredTitle] = useState();
    const [enderedAmount, setEnderedAmount] = useState();
    const [enderedDate, setEnderedDate] = useState();

    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value);
    };

    const amountChangeHandler = (event) => {
        setEnderedAmount(event.target.value);
    };

    const dateChangeHandler = (event) => {
        setEnderedDate(event.target.value);
    };
    const validText = 'Invalid message';

    const submitHandler = (event) => {
        event.preventDefault();

        const expenseData = {
          title: enderedTitle,
          amount: enderedAmount,
          date: new Date(enderedDate)  
        };

        props.onSaveExpenseData(expenseData);
        setEnteredTitle('');
        setEnderedAmount('');
        setEnderedDate('');
    };

    // const cancelHandler = () => {

    // };

    return (
        <form onSubmit={submitHandler}>
            <div className="form-content">
                <div className="new-expense__controls">
                    <div className="new-expense__control">
                        <label>Title</label>
                        <input type="text" value={enderedTitle} onChange={titleChangeHandler} />
                    </div>
                </div>
                <div className="new-expense__controls">
                    <div className="new-expense__control">
                        <label>Amount</label>
                        <input type="number" min="0.01" step="0.01" value={enderedAmount} onChange={amountChangeHandler} />
                    </div>
                </div>
                <div className="new-expense__controls">
                    <div className="new-expense__control">
                        <label>Date</label>
                        <input type="date" min="2019-01-01" max="2022-12-31" value={enderedDate} onChange={dateChangeHandler} />
                    </div>
                </div>
                <div className="new-expense__actions">
                    <button onClick={cancelHandler}>Cancel</button>
                    <button type="submit">Add Expense</button>
                </div>
            </div>
            <div className="form-content">
                <div className="new-expense__actions">
                    <button onClick={addNewHandler}>Add New Expense</button>
                </div>
            </div>
        </form>
    );
};

export default ExpenseForm;
