import React, { useState } from "react";

import ExpenseItem from "./ExpenseItem";
import ExpenseFilter from "./ExpensesFilter";
import Card from '../UI/Card';
import './Expenses.css';


function Expenses(props) {
    const [filteredYear, setYear] = useState('2020');

    let filterInfoText = '2021, 2022 & 2023';

    if(filteredYear === '2021'){
        filterInfoText = '2020, 2022 & 2023';
    } else if(filteredYear === '2022'){
        filterInfoText = '2020, 2021 & 2023';
    } else{
        filterInfoText = '2020, 2021 & 2022';
    }

    const saveYearHandler = (selectedYear) => {
        setYear(selectedYear);
    }

    return (
        <div>
            <Card className="expenses">
                <ExpenseFilter selected={filteredYear} onPassYearData={saveYearHandler} />
                <p>Data for years {filterInfoText} is hidden.</p>
                <ExpenseItem title={props.items[0].title} amount={props.items[0].amount} date={props.items[0].date}/>
                <ExpenseItem title={props.items[1].title} amount={props.items[1].amount} date={props.items[1].date}/>
                <ExpenseItem title={props.items[2].title} amount={props.items[2].amount} date={props.items[2].date}/>
                <ExpenseItem title={props.items[3].title} amount={props.items[3].amount} date={props.items[3].date}/>
            </Card>
        </div>
    )
}

export default Expenses;
